var doit = function () {
			var output = 1 ;
			if(node) {
				node.rawNode.nbVehicules_nodeList = [] ;
				if(node.children) {
					output = 0 ;
					node.children.forEach(function(child) {
						output += child.rawNode.nbVehicules ;
						dm_Tools.concat(node.rawNode.nbVehicules_nodeList, child.rawNode.nbVehicules_nodeList) ;
					} ) ;
				} else {
					node.rawNode.nbVehicules_nodeList.push(node) ;
				}
			}
			return output ;
		} ; return doit() ;

var doit = function () {
			var anneeColName = "Année" ;
			var output ;
			if(node) {
				if (node.children) {
					node.children.forEach(function (child) {
						var childYears = child.rawNode["nbVehicParAnnee"] ;
						if(childYears) {
							if(!output) output = {} ;
							for(var year in childYears) {
								if(year) {
									if(!output[year]) output[year] = 0 ;
									output[year] += childYears[year] ;
								}
							}
						}
					});
				} else {
					output = {} ;
					output[node.rawNode[anneeColName]] = 1 ;
				}
			}
			return output;
    	} ; return doit() ;

