var doit = function () {
			var output, tmp;
			var typeColName = "Type", modeleColName = "Modèle" ;
			if (node && !node.children && node.rawNode[modeleColName]) {
				output = {};
				tmp = {};
				tmp.count = 1;
				tmp.type = (dm_Tools.compare(node.rawNode[typeColName], 'Spécifique') || dm_Tools.compare(node.rawNode[typeColName], 'POIDS LOURD') || dm_Tools.compare(node.rawNode[typeColName], 'Transport commun')) ? 'Spécifique' : (''+node.rawNode[typeColName]).trim();
				output[(''+node.rawNode[modeleColName]).trim()] = tmp;
			} else if (node && node.children) {
				output = {};
				node.children.forEach(function (child) {
					for (var modele in child.rawNode.nbVehicParModele) {
						if(child.rawNode.nbVehicParModele[modele]) {
							if (output[modele]) {
								output[modele].count += child.rawNode.nbVehicParModele[modele].count;
							} else {
								tmp = {};
								tmp.type = child.rawNode.nbVehicParModele[modele].type;
								tmp.count = child.rawNode.nbVehicParModele[modele].count;
								output[modele] = tmp;
							}
						}
					}
				});
			}
			return output;
		} ; return doit() ;

var doit = function (obfuscateIt) {
			if(node.children) {
				var total = node.rawNode["nbVehicules"] ;
				var newName = node.name + ((total)?" (" + total + (obfuscateIt?"":
											((node.rawNode["nbVL"]?", "+node.rawNode["nbVL"]+" VL":"") + 
											 (node.rawNode["nb2roues"]?", "+node.rawNode["nb2roues"]+" 2r":""))) + 
											")":"") ;
				node.rawNode['displayName'] = newName ;
			}
		}; return doit(true) ;

var doit = function (obfuscateIt, us) {
			if(!obfuscateIt) return ;

			if(!nodes.obfuscateIdxes) nodes.obfuscateIdxes = [0, 0, 0, 0, 0, 0, 0, 0, 0] ;
			var nameIdx = " " + ++nodes.obfuscateIdxes[node.level] ;

			var randomImmat = function(immat) {
				var output = '', charI ;
				for(var i=0; i<immat.length; i++) {
					charI = immat[i] ;
					if(charI == ' ') {
						output += ' ' ;
					} else if(!isNaN(parseInt(charI))) {
						output += dm_Tools.random(1) ;
					} else {
						output += dm_Tools.randomString(1, "ABCDEFGHIJKLMNOPQRSTUVWXYZ") ;
					}
				}
				return output ;
			}

			if(node.level == 0) {
				// flush dm_Tools.trash
				dm_Tools.trash = null ;
				nodes.obfuscateIdxes = null ;
				node.name = (us)?"Assets":"Véhicules" ;
			}
			if(node.level == 1) {
				node.name = "Site" + nameIdx ;
			} else if(node.level == 2) {
				node.name = ((us)?"Entity":"Entité") + nameIdx ; 
			} else if(node.level == 3) {
				node.name = ((us)?"Department":"Département") + nameIdx ; 
			} else if(node.level == 4) {
				node.name = ((us)?"Business unit":"Direction") + nameIdx ; 
			} else if(node.level == 5) {
				node.name = "Service" + nameIdx ; 
			} else if(node.level == 6) {
				node.name = ((us)?"Project":"Unité") + nameIdx ; 
			} else if(node.level == 7) {
				node.name = ((us)?"Team":"Equipe") + nameIdx ; 
			} else if(node.level == 8) {
				node.name = "Agent" + nameIdx ;
				node.rawNode["Nom"] = ((us)?"LastName ":"Nom ") + nameIdx ;
				node.rawNode["Pr_nom"] = ((us)?"FirstName ":"Prénom ") + nameIdx ;
				node.rawNode["Immat"] = randomImmat(node.rawNode["Immat"]) ;
				if(us) {
 					if(dm_Tools.compare(node.rawNode["Type"], 'Deux roues')) node.rawNode["Type_en"] = "Scooter" ;
 					if(dm_Tools.compare(node.rawNode["Type"], 'Véhicule léger')) node.rawNode["Type_en"] = "Car" ;
 					if(dm_Tools.compare(node.rawNode["Type"], 'Utilitaire léger')) node.rawNode["Type_en"] = "Commercial vehicule" ;
 					if(dm_Tools.compare(node.rawNode["Type"], 'Utilitaire')) node.rawNode["Type_en"] = "Truck" ;
 					if(dm_Tools.compare(node.rawNode["Type"], 'Spécifique') ||
 					   dm_Tools.compare(node.rawNode["Type"], 'POIDS LOURD') ||
 					   dm_Tools.compare(node.rawNode["Type"], 'Transport commun')) node.rawNode["Type_en"] = "Specific vehicule" ;

 					if(dm_Tools.compare(node.rawNode["Autorisation de remisage"], 'OUI')) node.rawNode["Home storage"] = "YES" ;
 					if(dm_Tools.compare(node.rawNode["Autorisation de remisage"], 'NON')) node.rawNode["Home storage"] = "NO" ;

 					if(dm_Tools.compare(node.rawNode["Carte essence"], 'OUI')) node.rawNode["Gas card"] = "YES" ;
 					if(dm_Tools.compare(node.rawNode["Carte essence"], 'NON')) node.rawNode["Gas card"] = "NO" ;
				}
				node.rawNode["Année"] = "" + Math.min(new Date().getFullYear(), (parseInt(node.rawNode["Année"]) + dm_Tools.random(1, false, 4, -3))) ;
				node.rawNode["Total compteur km ou heures ATAL"] = Math.max(0, parseInt(node.rawNode["Total compteur km ou heures ATAL"]) + dm_Tools.random(4, false, 20000, -3000)) ;
				node.rawNode["Total km par an"] = Math.max(0, parseInt(node.rawNode["Total km par an"]) + dm_Tools.random(4, false, 5000, -1000)) ;

				if(dm_Tools.compare(node.rawNode["Autorisation de remisage"], 'NON')) {
					node.rawNode["Nb de trajets par jour"] = null ;
					node.rawNode["km Aller simple"] = null ;
					node.rawNode["Total km remisage par jour"] = null ;
					node.rawNode["nb de jours de remisage par an"] = null ;
					node.rawNode["Total km remisage par an"] = null ;
					node.rawNode["Total km hors remisage"] = node.rawNode["Total km par an"] ;
				} else {
					var totalKm = parseInt(node.rawNode["Total km par an"]) ;
					var nbTrajets = parseInt(node.rawNode["Nb de trajets par jour"]) ;
					var kmAller ;
					var nbJoursRemisage = parseInt(node.rawNode["nb de jours de remisage par an"]) ;
					node.rawNode["Total km remisage par an"] = dm_Tools.random(("" + totalKm).length, false, totalKm * 2/3) ;
					node.rawNode["Total km hors remisage"] = totalKm - node.rawNode["Total km remisage par an"] ;
					node.rawNode["Total km remisage par jour"] = node.rawNode["Total km remisage par an"] / nbJoursRemisage ;
					node.rawNode["km Aller simple"] = node.rawNode["Total km remisage par jour"] / nbTrajets ;
				}
			}
			var updateNode = function(node) {
				var parent = node ;
				while(parent) {
					if(parent.level == 1) {
						node.rawNode["Libell__Ville_ou_CASA"] = parent.name ;
					}
					if(parent.level == 2) {
						node.rawNode["Libell__DGS_ou_CMUN"] = parent.name ;
					}
					if(parent.level == 3) {
						node.rawNode["Libell__DGA"] = parent.name ;
					}
					if(parent.level == 4) {
						node.rawNode["Libell__Direction"] = parent.name ;
					}
					if(parent.level == 5) {
						node.rawNode["Libell__Service"] = parent.name ;
					}
					if(parent.level == 6) {
						node.rawNode["Libell__Unit_"] = parent.name ;
					}
					if(parent.level == 7) {
						node.rawNode["Libell__Equipe"] = parent.name ;
					}
					parent = parent.parent ;
				}
			}

			node.rawNode["Réforme"] = null ;
			node.rawNode["Observations"] = null ;

			updateNode(node) ;
		} ; doit(true, true) ;

