				node.rawNode[propName + ".content.target"] = 'dm_rootDatamap|freeDatamap|FranceLabs search|Search by origin' ;
					//"'http://127.0.0.1:8080/francellio-solr/FileShare/graph?q=' + '" + key + "' + ' ' +" + "dm_Tools.getNodePath(node, ' ', null, 'originalName', 2)" ;
			}
		}; return doit(false, true, 'http://127.0.0.1:8080/francellio-solr/FileShare/graph?q.op=AND&q=', 'http://127.0.0.1:8080/AntibesSearch/search.jsp?searchType=allWords&query=') ;

