var totalKmColName = 'Number of km (or hours)', doit = function (colonne, ranges, isRemisage, us) {
			var output, elt, vehicParKmRange = [] ;
			var remisageColName = 'Autorisation de remisage' ;
			var uniteKmColName = 'Unité km ou heures' ;
			for(var i=0; i<ranges.length+1; i++) {
				vehicParKmRange.push(0) ;
			}
			function getRangeIdx(value) {
				var output, rangeMax ;
				var i ;
				for(i=0; i<ranges.length; i++) {
					rangeMax = ranges[i] ;
					if(!output && (value <= rangeMax)) {
						output = i ;
					}
				}
				if(value > rangeMax) output = i ;
				//console.log(value+' km       > '+ranges[output-1]+' et < '+ranges[output]);
				return output ;
			}
			if(node) {
				var leaves = dm_Tools.getChildrenAtLevel(node) ;
				if(leaves) {
					leaves.forEach(function(leaf) {
						var unite = leaf.rawNode[uniteKmColName] ;
						var km = leaf.rawNode[colonne] ;
						var rangeIdx ;
						var remisageValue = leaf.rawNode[remisageColName] ;
						var takeit = true ;
						if(isRemisage) {
							takeit = remisageValue && (remisageValue.toLowerCase() == 'oui') ;
						}
						if(unite && (unite.toLowerCase() == "km") && !isNaN(km) && takeit) {
							rangeIdx = getRangeIdx(km) ;
							vehicParKmRange[rangeIdx]++ ;
						}
					}) ;
					for(var i=0; i<vehicParKmRange.length; i++) {
						nbVehic = vehicParKmRange[i] ;
						if(nbVehic > 0) {
							if (!output) {
								output = [] ;
							}
							elt = {} ;
							output.push(elt);
							elt.value = nbVehic ;
							elt.unit = (us)?'vehicules ':'véhicules ' ;
							if(i == 0) {
								elt.unit += ((us)?'with less than ':'à moins de ') + ranges[i] + ' km' ;
							} else if(i == ranges.length) {
								elt.unit += ((us)?'with more than ':'avec plus de ') + ranges[i-1] + ' km' ;
							} else {
								elt.unit += ((us)?'between ':'entre ') + ranges[i-1] + ((us)?' and ':' et ') + ranges[i] + ' km' ;
							}
						}
					} ;
				}
			}
			return output ;			
		} ; return doit(totalKmColName, [0, 10000, 20000, 40000, 60000, 80000, 100000, 150000, 200000], false, true) ;

